const currentposturl = "";
const maxpost = 6; // 表示記事数
const rphead = "関連記事"; // タイトル

//
let rpurl = [];
let rptitle = [];
let rpthumb = [];
let items = [];

function loadrp(json) {
  // フィードデータ取得
  function getData() {
    let entry, posturl, posttitle, postimg;
    for (let i = 0; i < json.feed.entry.length; i++) {
      entry = json.feed.entry[i];
      for (let j = 0; j < entry.link.length; j++) {
        if (entry.link[j].rel == 'alternate') {
          posturl = entry.link[j].href;
          break;
        }
      }
      posttitle = entry.title.$t;
      if ("media$thumbnail" in entry) {
        postimg = entry.media$thumbnail.url;
        if (postimg.match(/s72-c/)) {
          postimg = postimg.replace('s72-c', 's250');
        } else if (postimg.match(/default.jpg/)) {
          postimg = postimg.replace('default.jpg', 'mqdefault.jpg');
        }
      } else {
        postimg = "https://lh3.googleusercontent.com/gOUrtHeipHB99xATQmDBqQImdCbjt7RuBe93nz1mQp-f5J6_0wWA9_5jFxV8KY7Q7Dl-VOpGR9Z5SrFE8id9aoU9yYa7JTSgb6HVJ11Ifn9yORWmjSOsiOeZm1nW9HoooCUVxV-whg=s250";
      }
      rpurl.push(posturl);
      rptitle.push(posttitle);
      rpthumb.push(postimg);
    }
    // 現在の投稿を除外
    for (let k = 0; k < rpurl.length; k++) {
      if (rpurl[k] == currentposturl) {
        rpurl.splice(k, 1);
        rptitle.splice(k, 1);
        rpthumb.splice(k, 1);
        break;
      }
    }
  }
  getData();
  randomize();
  displayrp();
}

// ランダムインデックス生成
function randomize() {
  let idx;
  let a = [...Array(rpurl.length).keys()];
  while (a.length > 0) {
    idx = Math.floor(Math.random() * a.length);
    items.push(a[idx]);
    a.splice(idx, 1);
  }
}

// 関連記事の書き込み
function displayrp() {
  let rpHTML;
  let i = 0;
  if (items.length > 0) {
    rpHTML = '<div class="related-text">' + rphead + '</div><div class="p-container">';
    while (i < items.length && i < maxpost) {
      rpHTML += '<a href="' + rpurl[items[i]] + '"><figure class="related-img"><img loading="lazy" alt="" src="' + rpthumb[items[i]] + '"/></figure><div class="related-title">' + rptitle[items[i]] + '</div></a>';
      i++;
    }
    rpHTML += '</div>';
    document.getElementById("related-posts").innerHTML = rpHTML;
  }
}
