function loadpager(json) {
  function getPostData() {
    var entry, posttitle, posturl, postimg;
    for (var i = 0; i < json.feed.entry.length; i++) {
      entry = json.feed.entry[i];
      posttitle = entry.title.$t;
      for (var k = 0; k < entry.link.length; k++) {
        if (entry.link[k].rel == 'alternate') {
          posturl = entry.link[k].href;
          break;
        }
      }
      if ("media$thumbnail" in entry) {
        postimg = entry.media$thumbnail.url;
      } else {
        // NoImage画像
        postimg = "https://lh3.googleusercontent.com/gOUrtHeipHB99xATQmDBqQImdCbjt7RuBe93nz1mQp-f5J6_0wWA9_5jFxV8KY7Q7Dl-VOpGR9Z5SrFE8id9aoU9yYa7JTSgb6HVJ11Ifn9yORWmjSOsiOeZm1nW9HoooCUVxV-whg=s100"
      }
      postTitle.push(posttitle);
      postUrl.push(posturl);
      postImg.push(postimg);
    }
  }
  getPostData();
  displayPager();
}

function displayPager() {
  var current;
  var pagerHTML = '';
  // 現在の投稿を特定
  for (var i = 0; i < postTitle.length; i++) {
    if (postUrl[i] == currenturl) {
      current = i;
    }
  }
  if (current < postTitle.length - 1) {
    // 前の投稿へのリンク
    pagerHTML += '<div class="adjacent-nav-prev"><a href="' + postUrl[current+1] + '">';
    pagerHTML += '<div class="prev-next-img" style="background-image: url(' + postImg[current+1] + ')"><div class="prev-label">前の記事</div></div>';
    pagerHTML += '<div class="adjacent-right"><p class="prev-title">' + postTitle[current+1] + '</p></div>';
    pagerHTML += '</a></div>';
  } else {
    // トップへのリンク
    pagerHTML += '<div class="adjacent-nav-next-none"><a href="' + topurl + '">';
    pagerHTML += '<p>ホームへ</p>';
    pagerHTML += '</a></div>';
  }
  if (current > 0) {
    // 次の投稿へのリンク
    pagerHTML += '<div class="adjacent-nav-next"><a href="' + postUrl[current-1] + '">';
    pagerHTML += '<div class="adjacent-right"><p class="next-title">' + postTitle[current-1] + '</p></div>';
    pagerHTML += '<div class="prev-next-img" style="background-image: url(' + postImg[current-1] + ')"><div class="next-label">次の記事</div></div>';
    pagerHTML += '</a></div>';
  } else {
    // トップへのリンク
    pagerHTML += '<div class="adjacent-nav-next-none"><a href="' + topurl + '">';
    pagerHTML += '<p>ホームへ</p>';
    pagerHTML += '</a></div>';
  }
  document.getElementById("adjacent-nav").innerHTML = pagerHTML;
}
