window.addEventListener('DOMContentLoaded', function(){
  var pagetop = $('#pagetop');
  var pagebottom = $('#pagebottom');
  var toTop = 1000;//最上部ヘの到達時間(ミリ秒)
  var toBottom = 1000;//最下部ヘの到達時間
  $(window).scroll(function () {
      if ($(this).scrollTop() > 1000) {
          pagetop.fadeIn();
      } else {
          pagetop.fadeOut();
      }
      if ($(this).scrollTop() > 0 && $(this).scrollTop()<$(document).height()-1200) {
          pagebottom.fadeIn();
      } else {
          pagebottom.fadeOut();
      }
  });
  pagetop.click(function () {
      $('body, html').animate({ scrollTop: 0 }, toTop);
  });
  pagebottom.click(function () {
      $('body, html').animate({ scrollTop: $(document).height()}, toBottom);
  });
});
// 外部サイトへのリンクにnoopener noreferrerを設定する
const add_noopener_to_external_link = () => {
  Array.from(document.getElementsByTagName("a"))
    .filter((a) => a.host !== window.location.host) // サイト内遷移ははじく
    .forEach((a) => a.setAttribute('rel', 'noopener noreferrer'))
}
document.addEventListener('DOMContentLoaded', add_noopener_to_external_link, false);
